<?php

/**
 * The VGSR Ketel1 Widget Class
 * 
 * @package VGSR Widgets
 * @subpackage Ketel1
 */

// Exit if accessed directly
defined( 'ABSPATH' ) || exit;

/**
 * The VGSR Ketel1 Widget Class
 * 
 * @since 1.0.0
 */
class VGSR_Ketel1_Widget extends WP_Widget {

	/**
	 * Construct the widget
	 *
	 * @since 1.0.0
	 */
	public function __construct() {
		parent::__construct(
			'vgsr-ketel1',
			__( 'VGSR Ketel1', 'vgsr-widgets' ),
			array(
				'description' => __( 'Ketel1 logo, linked.', 'vgsr-widgets' ),
				'classname'   =>  'vgsr-ketel1'
			)
		);
	}

	/**
	 * Output the widget's display contents
	 *
	 * @since 1.0.0
	 *
	 * @uses get_post()
	 * @uses get_permalink()
	 *
	 * @param array $args Sidebar markup arguments
	 * @param array $instance Widget's instance settings
	 */
	public function widget( $args, $instance ) {

		// Wrap widget
		echo $args['before_widget'];

		// Define the image link url
		if ( $instance['post_id'] && $post = get_post( $instance['post_id'] ) ) {
			$url = get_permalink( $post );
		} else {
			$url = 'http://www.ketel1.nl';
		}

		// Display the logo. Use data-uri for image
		printf( '<p><a href="%s" target="_blank" style="display: inline-block; width: 100%%; background: #4B1B07; vertical-align: bottom; text-align: center;">%s</a></p>', esc_url( $url ), '<img alt="Logo Ketel1" src="data:image/png;base64," />' );

		// End wrap widget
		echo $args['after_widget'];
	}

	/**
	 * Output the widget's form contents
	 *
	 * @since 1.0.0
	 *
	 * @uses wp_dropdown_pages()
	 * @param array $instance Widget's instance settings
	 */
	public function form( $instance ) { ?>

		<p>
			<label for="<?php echo $this->get_field_id( 'post_id' ); ?>"><?php esc_html_e( 'Link to', 'vgsr-widgets' ); ?>:</label>
			<?php wp_dropdown_pages( array(
				'id'                    => $this->get_field_id( 'post_id' ),
				'name'                  => $this->get_field_name( 'post_id' ),
				'show_option_no_change' => '&mdash; www.ketel1.nl &mdash;',
			) ); ?>
		</p>

		<?php
	}

	/**
	 * Update the widget's settings
	 *
	 * @since 1.0.0
	 * 
	 * @param array $new_instance New settings
	 * @param array $old_instance Old settings
	 * @return array Updated settings
	 */
	public function update( $new_instance, $old_instance ) {

		// Parse post ID
		$new_instance['post_id'] = get_post( $new_instance['post_id'] ) ? $new_instance['post_id'] : 0;

		return $new_instance;
	}
}
